basestringfield.script:
					editmode = false;

					function setEditMode(state)
						if state then
							editmode = true;
							resetMenuItems();
							registerMenuItem("Stop editing", "stopedit", 5);
							setFocus();
							setCursorPosition(#getValue()+1);
							setSelectionPosition(0);
						else
							editmode = false;
							resetMenuItems();
							registerMenuItem("Edit", "edit", 4);
						end
					end
					
					function onHover(oncontrol)
						if not editmode then
							setUnderline(oncontrol);
						end
					end					
					
					function onClickDown(button, x, y)
						if not editmode then
							return true;
						end
					end
					
					function onClickRelease(button, x, y)
						if not editmode then
							if self.activate then
								self.activate();
							elseif linktarget then
								window[linktarget[1]].activate();
							end
						end
						return true;
					end					
					
					function onMenuSelection(...)
						setEditMode(not editmode);
					end
					
					function onLoseFocus()
						setEditMode(false);
					end
					
					function onInit()
						setEditMode(false);
					end
