	function onInit()
		if User.isHost() then
			DieBoxManager.registerGMHiddenControl(self);
		end
	end

	function onValueChanged()
		window.dieboxcontrol.onHiddenButtonPress(self);
	end					
	
	function onDrop(x, y, draginfo)
		window.dieboxcontrol.onDrop(x, y, draginfo);
		return true;
	end
